mapper.select:
select distinct m.id, m.parent_id, m.menu_name, m.path, m.component, m.query, m.visible, m.status, ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time, m.create_user_id,m.update_time,m.update_user_id, eu1.nick_name as createUserName, eu2.nick_name as updateUserName from easy_menu m left join easy_role_menu rm on m.id = rm.menu_id left join easy_user_role ur on rm.role_id = ur.role_id left join easy_role ro on ur.role_id = ro.id left join easy_user eu1 on m.create_user_id = eu1.id left join easy_user eu2 on m.update_user_id = eu2.id
AND ur.user_id = #{userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
AND m.status = #{status}
order by m.parent_id, m.order_num

select m.id from easy_menu m left join easy_role_menu rm on m.id = rm.menu_id where rm.role_id = #{roleId}
and m.id not in (select m.parent_id from easy_menu m inner join easy_role_menu rm on m.id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num
