mapper.delete:
delete from easy_slf4j_logging where log_time <= DATE_SUB(CURDATE(), INTERVAL 6 MONTH);
delete from easy_login_log where login_time <= DATE_SUB(CURDATE(), INTERVAL 6 MONTH);
delete from easy_oper_log where oper_time <= DATE_SUB(CURDATE(), INTERVAL 6 MONTH);

mapper.select:
select
t1.*
from easy_slf4j_logging t1

    
        and t1.track_id like CONCAT('%', #{query.trackId}, '%')
    
    
        and t1.log_class like CONCAT('%', #{query.className}, '%')
    
    
        and t1.log_level = #{query.logLevel}
    
    
        and t1.log_time >= #{query.beginTime}
    
    
        and t1.log_time <= #{query.endTime}
    

order by t1.log_time desc
